import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

import { userActions } from '../../../_actions';

class UsersList extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      currentSelectedId: props.auth.user.id
    };
  }

  onUserSelect(id) {
    this.props.dispatch(userActions.getOne(id));
    this.setState({
      currentSelectedId: id
    });
  }

  render() {
      const { auth, users } = this.props;
      return (
        <div className="user-list">
            {users && users.loading &&
                <img src="data:image/gif;base64," />
            }
            {users.err &&
              <div>{users.err}</div>
            }
            {users.items && 
              <ul>
                {users.items.map((user) =>
                  <li
                    key={user.id}
                    className={`users-item${ this.state.currentSelectedId === user.id ? ' active' : ''}`}
                    onClick={() => this.onUserSelect(user.id)}
                  >
                    <div className="avatar">
                      {user.deleting &&
                          <img src="data:image/gif;base64," />
                      }
                      {user.initials}
                    </div>
                    <div className="name">{user.username}</div>
                  </li>
                )}
              </ul>
            }
        </div>
      );
  }
}

UsersList.propTypes = {
  auth: PropTypes.object,
  users: PropTypes.object
}

function mapStateToProps(state) {
  const { authentication, users } = state;
  return {
    auth: authentication,
    users
  };
  return state;
}

const connectedUsersList = connect(mapStateToProps)(UsersList);
export { connectedUsersList as UsersList };