 */

function lightboxOpen(id) {
  return dispatch => {
    dispatch(open(id))
  }

  function open(id) { return { type: adminConstants.UI_LIGHTBOX_OPEN, id } }
}

function lightboxClose() {
  return dispatch => {
    dispatch(close())
