      const img = new Image(25, 25)
      img.onload = () => connectDragPreview(img)
      if (mime.includes('image')) {
        img.src = thumbs.icon
      }
      if (mime.includes('video') && videos) {
        img.src = videos.thumbs.medium
      }
    }
  }

  openLightbox(id) {
    this.props.dispatch(adminUiActions.lightboxOpen(id))
  }

