    const {
      classes, uploader, connectDragSource,
      item_width, item_height, gap_width, gap_height,
      width, height,
      id, media_id,
      status, fromServer,
      mime, filename, filesize,
      metadata, rekognition_labels, rekognition_text,
      thumbs, videos
    } = this.props

    let itemWidth = item_width
