        id={ id }
        width={ mediaSize.width }
        height={ mediaSize.height }
        mime={ mime }
        videos={ videos }
        thumbs={ thumbs }
        metadata={ metadata }
        text={ rekognition_text.text }
        onClick={ () => this.openLightbox(id) }
      />
