    else {
      thumb = <Thumbnail
        id={ id }
        width={ itemWidth }
        height={ itemHeight }
        mime={ mime }
        videos={ videos }
        uploader={ uploader }
      />
    }

    return connectDragSource(
      <li
        className={ classes.item }
        style={{
          height: item_height,
          width: item_width,
          marginRight: gap_width,
          marginBottom: gap_height
        }}
      >
        <Paper className={ classes.paperRoot }>

          <div
            className={ classes.thumbWrapper }
            style={{
              width: itemWidth,
              height: itemHeight
            }}
          >
            { thumb }
          </div>

          <ProgressBar
            id={ id }
            uploader={ uploader }
          />

          <div className={ classes.footer }>
            <div className={ classes.statusBar }>

              <Status
                id={ id }
                uploader={ uploader }
                fromServer={ fromServer }
                className={ classes.nameSizeText }
              />
              {media_id &&
                <div className={ classes.statusIcons }>
                  {rekognition_text &&
                    <StatusRekognitionTextIcon
                      rekognition_text={ rekognition_text }
                      id={ id }
                      media_id={ media_id }
                      className={ classes.statusIcon }
                    />
                  }
                  {mime && rekognition_labels &&
                    <StatusRekognitionLabelsIcon
                      rekognition_labels={ rekognition_labels }
                      mime={ mime }
                      id={ id }
                      media_id={ media_id }
                      className={ classes.statusIcon }
                    />
                  }
                  {metadata &&
                    <StatusMetadataIcon
                      metadata={ metadata }
                      id={ id }
                      media_id={ media_id }
                      className={ classes.statusIcon }
                    />
                  }
                  {thumbs &&
                    <StatusGenerateImageThumbsIcon
                      thumbs={ thumbs }
                      id={ id }
                      media_id={ media_id }
                      className={ classes.statusIcon }
                    />
                  }
                  {videos &&
                    <StatusGenerateVideosIcon
                      videos={ videos }
                      id={ id }
                      media_id={ media_id }
                      className={ classes.statusIcon }
                    />
                  }
                  {status === 'upload successful' &&
                    <div
                      className="icon success"
                      data-tip="Successfuly uploaded"
                    >
                      <IoCheckmarkCircled />
                      <ReactTooltip />
                    </div>
                  }
                  {status === 'upload failed' &&
                    <div
                      className="icon failed"
                      data-tip="Error saving file"
                    >
                      <IoBug />
                      <ReactTooltip />
                    </div>
                  }
                </div>
              }
            </div>

            <div className={ classes.nameSizeWrapper }>

              <Filename
                filename={ filename }
                width={ itemWidth - 90 }
                className={ classes.nameSizeText }
              />

              <Filesize
                filesize={ filesize }
                className={ classes.nameSizeText }
              />

            </div>

          </div>

          {media_id &&
            <RemoveButton
              media_id={ media_id }
            />
          }
        </Paper>

      </li>
    )
  }
}

