import objectAssign from 'object-assign'
import React, { Component } from 'react'
import PropTypes from 'prop-types'

import Typography from '@material-ui/core/Typography'

class Status extends Component {

  constructor(props) {
    super(props)

    this.state = {
      status: '',
      text: objectAssign({}, Status.defaultProps.text, props.text || {})
    }

    this._onStatusChange = (id, oldStatus, newStatus) => {
      if (id === this.props.id && !this._unmounted) {
        const newStatusToDisplay = getStatusToDisplay({
          displayMap: this.state.text,
          status: newStatus
        })

        newStatusToDisplay && this.setState({ status: newStatusToDisplay })
      }
    }
  }

  componentDidMount() {
    this.props.uploader.on('statusChange', this._onStatusChange)
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.text) {
      this.setState({
        text: objectAssign({}, this.state.text, nextProps.text)
      })
    }
  }

  componentWillUnmount() {
    this._unmounted = true
    this.props.uploader.off('statusChange', this._onStatusChange)
  }

  render() {

    const { className, fromServer } = this.props

    const status = fromServer ? 'On server' : this.state.status

    return (
      <Typography
        className={ className }
        variant="caption"
      >
        { status }
      </Typography>
    )
  }
}

Status.propTypes = {
  className: PropTypes.string,
  fromServer: PropTypes.bool,
  id: PropTypes.number.isRequired,
  text: PropTypes.shape({
    canceled: PropTypes.string,
    deleted: PropTypes.string,
    deleting: PropTypes.string,
    paused: PropTypes.string,
    queued: PropTypes.string,
    retrying_upload: PropTypes.string,
    submitting: PropTypes.string,
    uploading: PropTypes.string,
    upload_failed: PropTypes.string,
    upload_successful: PropTypes.string
  }),
  uploader: PropTypes.object.isRequired
}

Status.defaultProps = {
  className: '',
  fromServer: false,
  text: {
    canceled: 'Canceled',
    deleted: 'Deleted',
    deleting: 'Deleting...',
    paused: 'Paused',
    queued: 'Queued',
    retrying_upload: 'Retrying...',
    submitting: 'Submitting...',
    uploading: 'Uploading...',
    upload_failed: 'Failed',
    upload_successful: 'Completed'
  }
}

const getStatusToDisplay = ({ displayMap, status }) => {
  let key

  if (status.indexOf(' ') > 0) {
    const statusParts = status.split(' ')

    key = `${statusParts[0]}_${statusParts[1]}`
  }
  else {
    key = status
  }

  return displayMap[key]
}

export default Status

import React, { Component, Fragment } from 'react'
import PropTypes from 'prop-types'
import ReactPlayer from 'react-player'

import { Spinner } from 'Common'
import { IoAlertCircled } from 'react-icons/lib/io'

class Thumbnail extends Component {

  constructor() {
    super()

    this.state = {
      drawComplete: false
    }
  }

  componentDidMount() {

    const { id, height, width, mime } = this.props

    if (mime.includes('image')) {
      this.props.uploader.methods.drawThumbnail(
        id,
        this._canvas,
        Math.max(height, width)
      ).then(
        () => {
          this.setState({
            drawComplete: true,
            success: true,
            mime: 'image'
          })
        },
        () => {
          this.setState({
            drawComplete: true,
            success: false
          })
        }
      )
    }
    else if (mime.includes('video')) {
      this.setState({
        drawComplete: true,
        success: true,
        mime: 'video'
      })
    }
  }

  render() {
    const { width, height, mime, videos } = this.props
    if (mime.includes('image')) {
      return (
        <Fragment>
          <canvas
            className="thumbnail"
            hidden={ !this.state.drawComplete || this._failure }
            width={ width }
            height={ height }
            ref={ component => this._canvas = component }
          />
          { this._maybePlaceholder }
        </Fragment>
      )
    }
    else if (mime.includes('video')) {
      return (
        <div
          className="uploader-thumbnail video"
          style={{height: height, width: width}}
        >
          {videos &&
            <ReactPlayer
              url={ videos.video }
              controls={ true }
              width={ width }
              height={ height }
            />
          }
          { this._maybePlaceholder }
        </div>
      )
    }
    else {
      return <span />
    }
  }

  get _failure() {
    return this.state.drawComplete && !this.state.success
  }

  get _maybePlaceholder() {
    const style = {
      height: this.props.height,
      width: this.props.width
    }
    if (this._failure) {
      return (
        <div
          className="placeholder not-available"
          style={ style }
        >
          <div className="icon"><IoAlertCircled /></div>
          <div className="message">Can't show thumbnail</div>
        </div>
      )
    } else if (!this.state.drawComplete) {
      return (
        <div
          className="placeholder waiting"
          style={ style }
        >
          <Spinner type="thumbnail" size={ 50 } />
        </div>
      )
    }

    return <span />
  }
}

Thumbnail.propTypes = {
  id: PropTypes.number.isRequired,
  width: PropTypes.number.isRequired,
  height: PropTypes.number.isRequired,
  uploader: PropTypes.object.isRequired,
  mime: PropTypes.string.isRequired,
  videos: PropTypes.object
}

Thumbnail.defaultProps = {
  videos: null
}

export default Thumbnail

import React, { Component } from 'react'
import PropTypes from 'prop-types'
import ReactPlayer from 'react-player'

import { withStyles } from '@material-ui/core/styles'

import { drawCanvasImage } from 'Helpers'

const styles = theme => ({
  image: {
    cursor: `zoom-in`
  }
})

class ThumbnailSrv extends Component {

  constructor(props) {
    super(props)

    this.imageRef = React.createRef()
  }

  componentDidMount() {

    const { mime, thumbs, metadata, text } = this.props

    let canvas = this.imageRef.current
    if (mime.includes('image')) {
      // Draw summary icon image canvas
      let orientation = 1
      if (metadata && metadata.orientation) {
        orientation = parseInt(metadata.orientation)
      }
      drawCanvasImage(canvas, thumbs.thumb, orientation, text)
    }
  }

  componentWillReceiveProps(nextProps) {

    if (this.props.id != nextProps.id) {

      const { id, mime, thumbs, metadata, text } = nextProps

      let canvas = this.imageRef.current
      if (mime.includes('image')) {
        // Draw summary icon image canvas
        let orientation = 1
        if (metadata && metadata.orientation) {
          orientation = metadata.orientation
        }
        drawCanvasImage(canvas, thumbs.thumb, orientation, text)
      }
    }
  }

  render() {

    const { classes, onClick, id, width, height, mime, thumbs, videos } = this.props
    // console.log(id, thumbs.thumb)
    let item = <span>Unsuported media type</span>

    if (mime.includes('image')) {  
      item = <canvas
        ref={ this.imageRef }
        onClick={ onClick }
        width={ width }
        height={ height }
        className={ classes.image }
      />
    }

    else if (mime.includes('video')) {
      item = <ReactPlayer
        url={ videos.medium }
        controls={ true }
        width={ width }
        height={ height }
      />
    }

    return item
  }
}

ThumbnailSrv.propTypes = {
  classes: PropTypes.object.isRequired,
  onClick: PropTypes.func.isRequired,
  id: PropTypes.number.isRequired,
  width: PropTypes.number.isRequired,
  height: PropTypes.number.isRequired,
  mime: PropTypes.string,
  thumbs: PropTypes.object,
  videos: PropTypes.object,
  metadata: PropTypes.object,
  text: PropTypes.array
}

ThumbnailSrv.defaultProps = {
  mime: '',
  thumbs: null,
  videos: null,
  metadata: null,
  text: null
}

export default withStyles(styles)(ThumbnailSrv)

import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { connect } from 'react-redux'

import { withStyles } from '@material-ui/core/styles'
import List from '@material-ui/core/List'

import MediaItem from './MediaItem'

import { albumsActions } from 'Actions'

const styles = theme => ({
  list: {
    display: `flex`,
    flexWrap: `wrap`
  }
})

class MediaList extends Component {

  componentDidMount() {
    this.setMediaGrid(this.props)
  }

  componentWillReceiveProps(nextProps) {
    this.setMediaGrid(nextProps)
  }

  componentWillUnmount() {
    const { dispatch } = this.props
    dispatch(albumsActions.setMediaPagerPage(0))
  }

  setMediaGrid(props) {

    const { pager, wrapper_width, wrapper_height, dispatch } = props
    let { cols, rows } = pager

    if (wrapper_width < 480)
      cols = 1
    if (wrapper_width < 680 && wrapper_width >= 480)
      cols = 2
    else if (wrapper_width >= 680 && wrapper_width < 1000)
      cols = 3
    else if (wrapper_width >= 1000)
      cols = 4

    if (wrapper_height < 400)
      rows = 1
    else if (wrapper_height >= 400)
      rows = 2

    if (pager.cols != cols || pager.rows != rows)
      dispatch(albumsActions.setMediaPagerGrid(cols, rows))
  }

  render() {
    const { classes, files, pager, uploader, wrapper_width, wrapper_height } = this.props

    const { current_page, cols, rows, per_page} = pager

    const firstList = current_page * per_page
    const lastList = firstList + per_page
    const currentFiles = files.slice(firstList, lastList)

    let gap_width = 12
    let gap_height = 12

    let vertGaps = (cols + 1) * gap_width
    let horiGaps = (rows + 1) * gap_height

    let item_width = (wrapper_width - vertGaps) / cols
    let item_height = (wrapper_height - horiGaps) / rows

    return (
      <List
        className={ classes.list }
        disablePadding={ true }
        style={{
          width: wrapper_width,
          height: wrapper_height,
          paddingLeft: gap_width,
          paddingTop: gap_height
        }}
      >
        {currentFiles && currentFiles.map((file, i) => (
          <MediaItem
            key={ i }
            index={ i }
            uploader={ uploader }
            item_width={ item_width }
            item_height={ item_height }
            gap_width={ gap_width }
            gap_height={ gap_height }
            { ...file }
          />
        ))}
      </List>
    )
  }
}

MediaList.propTypes = {
  dispatch: PropTypes.func.isRequired,
  classes: PropTypes.object.isRequired,
  files: PropTypes.array.isRequired,
  wrapper_width: PropTypes.number.isRequired,
  wrapper_height: PropTypes.number.isRequired,
  uploader: PropTypes.object.isRequired,
  pager: PropTypes.shape({
    current_page: PropTypes.number,
    per_page: PropTypes.number
  })
}

MediaList.defaultProps = {
  pager: {
    current_page: 0,
    per_page: 8
  }
}

function mapStateToProps(state) {
  const { admin_albums } = state
  return {
    pager: admin_albums.selected_album.pager
  }
}

export default connect(mapStateToProps)(withStyles(styles)(MediaList))

import React, { Component } from 'react'
