import React, { Component } from 'react'
import PropTypes from 'prop-types'
import qq from 'fine-uploader/lib/dnd'

class Dropzone extends Component {

  componentDidMount() {
    this._registerDropzone()
  }

  componentDidUpdate() {
    this._registerDropzone()
  }

  componentWillUnmount() {
    this._qqDropzone && this._qqDropzone.dispose()
  }

  render() {
    const { uploader, ...elementProps } = this.props // eslint-disable-line no-unused-vars

    return (
      <div
        { ...getElementProps(this.props) }
        ref="dropZone"
      >
        { this.props.children }
      </div>
    )
  }

  _onDropError(errorCode, errorData) {
    console.error(errorCode, errorData)

    this.props.onDropError && this.props.onDropError(errorCode, errorData)
  }

  _onProcessingDroppedFilesComplete(files) {
    const { uploader } = this.props
    uploader.methods.addFiles(files)

    if (this.props.onProcessingDroppedFilesComplete) {
      this.props.onProcessingDroppedFilesComplete(files)
    }
  }

  _registerDropzone() {
    this._qqDropzone && this._qqDropzone.dispose()

    const dropzoneEl = this.refs.dropZone
    // console.log(this.props);
    this._qqDropzone = new qq.DragAndDrop({
      allowMultipleItems: !!this.props.multiple,
      callbacks: {
        dropError: this._onDropError.bind(this),
        processingDroppedFiles: this.props.onProcessingDroppedFiles || function() {},
        processingDroppedFilesComplete: this._onProcessingDroppedFilesComplete.bind(this)
      },
      classes: {
        dropActive: this.props.dropActiveClassName || ''
      },
      dropZoneElements: [dropzoneEl]
    })
  }
}

Dropzone.propTypes = {
  uploader: PropTypes.object.isRequired,
  children: PropTypes.node,
  dropActiveClassName: PropTypes.string,
  multiple: PropTypes.bool,
  onDropError: PropTypes.func,
  onProcessingDroppedFiles: PropTypes.func,
  onProcessingDroppedFilesComplete: PropTypes.func
}

Dropzone.defaultProps = {
  dropActiveClassName: 'active',
  children: <span />,
  multiple: false,
  onDropError: null,
  onProcessingDroppedFiles: null,
  onProcessingDroppedFilesComplete: null
}

const getElementProps = actualProps => {
  const actualPropsCopy = { ...actualProps }
  const expectedPropNames = Object.keys(Dropzone.propTypes)

  expectedPropNames.forEach(expectedPropName => delete actualPropsCopy[expectedPropName])
  return actualPropsCopy
}

export default Dropzone

import React, { Component, Fragment } from 'react'
import PropTypes from 'prop-types'
import { connect } from 'react-redux'
import HTML5Backend from 'react-dnd-html5-backend'
import { DragDropContextProvider } from 'react-dnd'
import Rnd from 'react-rnd'

import AlbumsList from './AlbumsList'
import AlbumInfo from './AlbumInfo'
import NewAlbum from '../Buttons/NewAlbum'

import { utilsActions, albumsActions } from 'Actions'

const styles = {
  rnd_wrapper: {
    display: `flex`,
    flexDirection: `column`,
    borderRight: `1px solid grey`
  }
}

/**
 * This is Albums.
 * @reactProps {!number} prop1 - this is prop1
 * @reactProps {string} prop2 - this is prop2
 */
class Albums extends Component {

  constructor(props) {
    super(props)

    this.state = {
      infoWidth: props.client_width - props.sidebar_width
    }

    const { selected_album_id, dispatch } = this.props
    dispatch(albumsActions.getOne(selected_album_id))
  }

  componentWillUnmount() {
    const { dispatch } = this.props
    dispatch(albumsActions.clearSelected())
  }

  onSidebarResize(e, direction, ref, delta, position) {
    const { dispatch } = this.props
    let width = ref.offsetWidth
    dispatch(utilsActions.setAdminSetting('sidebar_width', width))
  }

  onSidebarResizeEnd(e, direction, ref, delta, position) {
    const { dispatch } = this.props
    let width = ref.offsetWidth
    dispatch(utilsActions.saveAdminSetting('sidebar_width', width))
  }

  render() {

    const { client_width, client_height, sidebar_width } = this.props
    let info_width = client_width - sidebar_width
    let info_height = client_height - 90 // substract header and footer

    return (
      <DragDropContextProvider backend={ HTML5Backend }>
        <Fragment>
          <Rnd
            default={{
              width: sidebar_width,
              height: `100%`
            }}
            enableResizing={{
              top: false,
              right: true,
              bottom: false,
              left: false,
              topRight: false,
              bottomRight: false,
              bottomLeft: false,
              topLeft: false
            }}
            minWidth={ 150 }
            maxWidth={ 350 }
            disableDragging={ true }
            onResize={ this.onSidebarResize.bind(this) }
            onResizeStop={ this.onSidebarResizeEnd.bind(this) }
            style={ styles.rnd_wrapper }
          >
            <AlbumsList />
          </Rnd>

          <AlbumInfo
            width={ info_width }
            height={ info_height }
          />

          <NewAlbum />

        </Fragment>
      </DragDropContextProvider>
    )
  }
}

Albums.propTypes = {
  dispatch: PropTypes.func.isRequired,
  selected_album_id: PropTypes.number.isRequired,
  client_width: PropTypes.number.isRequired,
  client_height: PropTypes.number.isRequired,
  sidebar_width: PropTypes.number.isRequired
}

function mapStateToProps(state) {
  const { client, settings } = state
  return {
    selected_album_id: parseInt(settings.admin.selected_album),
    sidebar_width: parseInt(settings.admin.sidebar_width),
    client_width: client.screen.width,
    client_height: client.screen.height
  }
}

export default connect(mapStateToProps)(Albums)

