class UsersList extends Component {

  componentDidMount(){
    const { dispatch, users: { items } } = this.props

    dispatch(headerActions.setTitle('Users'))

    // load user from API only if none
    if (items && items.length === 0)
      dispatch(userActions.getList())
  }

  render() {

    const { classes, authId, users: { loading, err, items } } = this.props

    return (
      <Fragment>

        {loading &&
          <Spinner type="primary" size={ 70 } />
        }

        {err &&
          <div>{err}</div>
        }

        {items &&
          <Scrollbar className={ classes.scrollbar }>
            <List
              className={ classes.list }
              disablePadding={ true }
            >
              {items.map(user =>
                <UsersListItem key={ user.id } authId={ authId } { ...user } />
              )}
            </List>
          </Scrollbar>
        }

        <UserCreateButton />

      </Fragment>
    )
  }
}

UsersList.propTypes = {
  dispatch: PropTypes.func.isRequired,
  classes: PropTypes.object.isRequired,
  authId: PropTypes.number.isRequired,
  users: PropTypes.object.isRequired
}

function mapStateToProps(state) {
  const { auth, users } = state
  return {
    authId: auth.id,
    users: users.list
  }
}

export default connect(mapStateToProps)(withStyles(styles)(UsersList))

