const renderSelect = (props) => {

  const {
    classes, margin, fullWidth, label,
    input , options, multi, className,
    meta
  } = props

  const { name, value, onBlur, onChange, onFocus } = input
  const transformedValue = transformValue(value, options, multi)
  const { active, valid, error } = meta

  let rootClass = classes.root
  if (margin === 'dense')
    rootClass = classes.rootDense
  if (margin === 'normal')
    rootClass === rootNormal

  return (
    <div
      className={classNames(
        className,
        classes.root,
        rootClass,
        { [classes.fullWidth]: fullWidth }
      )}
    >
      {label &&
        <label
          className={classNames(
            classes.labelRoot,
            { [classes.labelError]: !valid },
            { [classes.labelActive]: active }
          )}
        >
          { label }
        </label>
      }

      <Select
        valueKey="value"
        name={ name }
        value={ transformedValue }
        multi={ multi }
        options={ options }
        onChange={multi
          ? multiChangeHandler(onChange)
          : singleChangeHandler(onChange)
        }
        onBlur={ () => onBlur(value) }
        onFocus={ onFocus }
        className={classNames(
          classes.selectRoot,
          { [classes.fullWidth]: fullWidth },
          { [classes.selectWithLabel]: label }
        )}
      />

      {!valid &&
        <Typography
          variant="caption"
          align="left"
          component="p"
          className={classNames(
            classes.helperRoot,
            { [classes.labelError]: !valid },
            { [classes.helperDense]: margin === 'dense' }
          )}
        >
          { error }
        </Typography>
      }

    </div>
  )
}

/**
 * onChange from Redux Form Field has to be called explicity.
 */
function singleChangeHandler(func) {
  return function handleSingleChange(value) {
    func(value ? value.value : '')
  }
}

/**
 * onBlur from Redux Form Field has to be called explicity.
 */
function multiChangeHandler(func) {
  return function handleMultiHandler(values) {
    func(values.map(value => value.value))
  }
}

/**
 * For single select, Redux Form keeps the value as a string, while React Select
 * wants the value in the form { value: "grape", label: "Grape" }
 *
 * * For multi select, Redux Form keeps the value as array of strings, while React Select
 * wants the array of values in the form [{ value: "grape", label: "Grape" }]
 */
function transformValue(value, options, multi) {
  if (multi && typeof value === 'string') return []

  const filteredOptions = options.filter(option => {
    return multi
      ? value.indexOf(option.value) !== -1
      : option.value === value
  })

  return multi ? filteredOptions : filteredOptions[0]
}

renderSelect.propTypes = {
  classes: PropTypes.object.isRequired,
  input: PropTypes.shape({
    name: PropTypes.string.isRequired,
    value: PropTypes.string.isRequired,
    onBlur: PropTypes.func.isRequired,
    onChange: PropTypes.func.isRequired,
    onFocus: PropTypes.func.isRequired,
  }).isRequired,
  meta: PropTypes.shape({
    valid: PropTypes.bool.isRequired,
    error: PropTypes.string
  }),
  options: PropTypes.array.isRequired,
  label: PropTypes.string,
  multi: PropTypes.bool,
  className: PropTypes.string,
  margin: PropTypes.string,
  fullWidth: PropTypes.bool
}

renderSelect.defaultProps = {
  meta: PropTypes.shape({
    error: null
  }),
  label: null,
  multi: false,
  className: '',
  margin: 'normal',
  fullWidth: true
}

export default withStyles(styles)(renderSelect)

import React from 'react'
import PropTypes from 'prop-types'
import TextField from '@material-ui/core/TextField'

const renderText = ({input, label, type, meta: {touched, error, invalid}, ...otherProps}, ctx) => {

  const { t } = ctx
  const translatedError = t(error)
  return (
    <TextField
      type={ type }
      label={ label }
      error={ touched && invalid }
      helperText={ touched && translatedError }
      { ...input }
      { ...otherProps }
    />

  )
}

renderText.contextTypes = {
  t: PropTypes.func
}

renderText.propTypes = {
  input: PropTypes.object.isRequired,
  label: PropTypes.string.isRequired,
  type: PropTypes.string,
  meta: PropTypes.object
}

renderText.defaultProps = {
  type: 'text',
  meta: {},
  margin: 'normal',
  fullWidth: true
}

export default renderText

