import FineUploaderS3 from 'fine-uploader-wrappers/s3'

import { baseServerUrl } from 'Helpers'

const allowedExtensions = ['jpeg', 'jpg', 'mp4']

export function mediaUploader(bucket, access_key, allowed = allowedExtensions) {

  return new FineUploaderS3({
    options: {
      request: {
        endpoint: bucket + '.s3.amazonaws.com',
        accessKey: access_key
      },
      signature: {
        endpoint: baseServerUrl + '/api/uploader/sign',
        version: 2
      },
      chunking: {
        enabled: true
      },
      resume: {
        enabled: true
      },
      objectProperties: {
        serverSideEncryption: true,
        key: function(fileId) {
          let name = this.getName(fileId)
          let rand = Math.floor((Math.random() * 9999999) + 1)
          let ext = name.substr(name.lastIndexOf('.') + 1)

          return 'media/'+Date.now().toString()+'-'+rand+'.'+ext.toLowerCase()
        }
      },
      uploadSuccess: {
        endpoint: baseServerUrl + '/api/uploader/success'
      },
      validation: {
        allowedExtensions: allowed,
        stopOnFirstInvalidFile: false
      }
    }
  })
}

import { albumsConstants } from 'Constants'

const initialState = {
  selected_album: {
    album: {},
    map: {
      edit_enabled: false
    },
    pager: {
      current_page: 0,
      cols: 1,
      rows: 1,
      per_page: 1
    }
  },
  list: {
    loading: false,
    err: false,
    items: []
  },
  dates: {
    loading: false,
    err: false,
    distinct_list: []
  }
}

export function adminAlbums(state = initialState, action) {
  switch (action.type) {

  /*
   * Album list reducers
   * calls GET_LIST_REQUEST, GET_LIST_SUCCESS, GET_LIST_FAILURE
   *       ADD_TO_LIST
   */

  case albumsConstants.GET_LIST_REQUEST:
    return {
      ...state,
      list: {
        loading: true
      }
    }
  case albumsConstants.GET_LIST_SUCCESS:
    return {
      ...state,
      list: {
        items: action.albums
      }
    }
  case albumsConstants.GET_LIST_FAILURE:
    return {
      ...state,
      list: {
        err: action.err
      }
    }
  case albumsConstants.ADD_TO_LIST:
    return {
      ...state,
      list: {
        items: [action.album, ...state.list.items]
      }
    }

  /*
   * Album list dates reducers
   * calls GET_LIST_DATES_REQUEST, GET_LIST_DATES_SUCCESS, GET_LIST_DATES_FAILURE
   */

  case albumsConstants.GET_LIST_DATES_REQUEST:
    return {
      ...state,
      dates: {
        loading: true
      }
    }
  case albumsConstants.GET_LIST_DATES_SUCCESS:
    return {
      ...state,
      dates: {
        distinct_list: action.dates
      }
    }
  case albumsConstants.GET_LIST_DATES_FAILURE:
    return {
      ...state,
      dates: {
        err: action.err
      }
    }


  /*
   * Album get one reducers
   * calls GET_ONE_REQUEST, GET_ONE_SUCCESS, GET_ONE_FAILURE
   */

  case albumsConstants.GET_ONE_REQUEST:
    return {
      ...state,
      selected_album: {
        ...state.selected_album,
        album: {
          loading: true
        }
      }
    }
  case albumsConstants.GET_ONE_SUCCESS:
    return {
      ...state,
      selected_album: {
        ...state.selected_album,
        album: action.album
      }
    }
  case albumsConstants.GET_ONE_FAILURE:
    return {
      ...state,
      selected_album: {
        ...state.selected_album,
        album: {
          err: action.err
        }
      }
    }


  /*
   * Album data reducers
   * calls RENAME, CHANGE_DATE
   */

  case albumsConstants.RENAME:
    return {
      ...state,
      selected_album: {
        album: {
          ...state.selected_album.album,
          name: action.payload.name
        }
      },
      // Also rename album in list
      list: {
        items: state.list.items.map(album => {
          if (album.id === action.payload.album_id) {
            const { ...albumCopy } = album
            return { ...albumCopy, name: action.payload.name }
          }
          return album
        })
      }
    }
  case albumsConstants.CHANGE_DATE:
    return {
      ...state,
      selected_album: {
        album: {
          ...state.selected_album.album,
          start_date: action.payload.start_date,
          end_date: action.payload.end_date
        }
      },
      // Also change album date in list
      list: {
        items: state.list.items.map(album => {
          if (album.id === action.payload.album_id) {
            const { ...albumCopy } = album
            return {
              ...albumCopy,
              start_date: action.payload.start_date,
              end_date: action.payload.end_date
            }
          }
          return album
        })
      }
    }


  /*
