      }
    }
  case frontConstants.GETLIST_LOAD_MORE:
    return {
      ...state,
      list: {
        items: [ ...state.list.items, ...action.albums ]
      }
    }
  case frontConstants.GETLIST_FAILURE:
    return {
      list: {
        err: action.err
      }
    }
  default:
