import { baseServerUrl } from 'Helpers'

export const loginService = {
  login,
  logout
}

function login(username, password) {
  const requestOptions = {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ username, password })
  }

  return fetch(`${baseServerUrl}/api/auth`, requestOptions)
    .then(response => {
      if (!response.ok) {
        return Promise.reject(response.statusText)
      }
      return response.json()
    })
    .then(res => {
      let user = res.data
      // login successful if there's a jwt token in the response
      if (user && user.token) {
        // store user details and jwt token in local storage to keep user logged in between page refreshes
        localStorage.setItem('user', JSON.stringify(user))
      }

      return res
    })
}

function logout() {
  // remove user from local storage to log user out
  localStorage.removeItem('user')
}

import request from 'superagent'

export const locationService = {
  getCurrentLocation
}

function getCurrentLocation(cb) {
  // Try HTML5 geolocation.
  if (navigator.geolocation) {
    navigator.geolocation.getCurrentPosition(position => {
      let loc = {
        lat: position.coords.latitude,
        lng: position.coords.longitude
      }
      cb(loc)
    }, () => {
      // If cant get current location
      getLocationFromApi(loc => {
        cb(loc)
      })

    })
  } else {
    // Browser doesn't support Geolocation
    getLocationFromApi(loc => {
      cb(loc)
    })
  }
}

function getLocationFromApi(cb) {
  let location = {
    lat: 0,
    lng: 0
  }
  request
    .get('http://ip-api.com/json')
    .end((err, res) => {
      if (res.status === 200 && res.body.status == 'success') {
        location.lat = res.body.lat
        location.lng = res.body.lon
      }
      cb(location)
    })
}

